import React, { useEffect } from 'react';
import styled from './dashboard.module.css';
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { cedi } from '../../utils/Icons';
import Chart from '../Chart/Chart';

function Dashboard() {
    const { totalExpenses, incomes, expenses, totalIncome, totalBalance, getIncomes, getExpenses } = useGlobalContext();

    useEffect(() => {
        getIncomes();
        getExpenses();
    }, []);

    return (
        <div className={styles.dashboard}>
            <InnerLayout>
                <h1>All Transactions</h1>
                <div className={styles.statsCon}>
                    <div className={styles.chartCon}>
                        <Chart />
                        <div className={styles.amountCon}>
                            <div className={styles.income}>
                                <h2>Total Income</h2>
                                <p>{cedi} {totalIncome()}</p>
                            </div>
                            <div className={styles.expense}>
                                <h2>Total Expense</h2>
                                <p>{cedi} {totalExpenses()}</p>
                            </div>
                            <div className={styles.balance}>
                                <h2>Total Balance</h2>
                                <p>{cedi} {totalBalance()}</p>
                            </div>
                        </div>
                    </div>
                    <div className={styles.historyCon}>
                        <History />
                        <h2 className={styles.salaryTitle}>
                            Min <span>Salary</span>Max
                        </h2>
                        <div className={styles.salaryItem}>
                            <p>${Math.min(...incomes.map(item => item.amount))}</p>
                            <p>${Math.max(...incomes.map(item => item.amount))}</p>
                        </div>
                        <h2 className={styles.salaryTitle}>
                            Min <span>Expense</span>Max
                        </h2>
                        <div className={styles.salaryItem}>
                            <p>${Math.min(...expenses.map(item => item.amount))}</p>
                            <p>${Math.max(...expenses.map(item => item.amount))}</p>
                        </div>
                    </div>
                </div>
            </InnerLayout>
        </div>
    );
}

export default Dashboard;
